import React from "react";
import styled from "styled-components";

const Container = styled.div``;

const CategoryItem = ({ item }) => {
  return (
    <Container>
      <Image src={item.img}></Image>
      <Info>
        <Title>{item.title}</Title>
        <Button>SHOP</Button>
      </Info>
    </Container>
  );
};

export default CategoryItem;
